package com.example.bietjhansiapp.ui.about;

import android.os.Bundle;

import androidx.fragment.app.Fragment;
import androidx.viewpager.widget.PagerAdapter;
import androidx.viewpager.widget.ViewPager;

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ImageView;

import com.bumptech.glide.Glide;
import com.example.bietjhansiapp.R;

import java.util.ArrayList;
import java.util.List;


public class AboutFragment extends Fragment {

    private ViewPager viewPager;
    private BranchAdapter branchAdapter;
    private List<BranchModel> list;
    private ImageView collegeImage;

    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        // Inflate the layout for this fragment
        View view= inflater.inflate(R.layout.fragment_about, container, false);

        list=new ArrayList<>();
        list.add(new BranchModel(R.drawable.ic_cse,"Computer Science & Engineering",R.string.cse));
        list.add(new BranchModel(R.drawable.ic_it,"Information Technolog",R.string.it));
        list.add(new BranchModel(R.drawable.ic_ece,"Electronics Communication & Engineering",R.string.ece));
        list.add(new BranchModel(R.drawable.ic_ee,"Electrical Engineering",R.string.ee));
        list.add(new BranchModel(R.drawable.ic_me,"Mechanical Engineering",R.string.me));
        list.add(new BranchModel(R.drawable.ic_ce,"Civil Engineering",R.string.ce));
        list.add(new BranchModel(R.drawable.ic_che,"Chemical Engineering",R.string.che));
        list.add(new BranchModel(R.drawable.ic_mba,"Master of Business Administration",R.string.mba));

        branchAdapter=new BranchAdapter(getContext(),list);
        viewPager=view.findViewById(R.id.viewPager);
        viewPager.setAdapter(branchAdapter);

        collegeImage=view.findViewById(R.id.college_image);
//        collegeImage.setImageResource(R.drawable.biet);
        Glide.with(getContext()).load("data:image/jpeg;base64,")
                .into(collegeImage);

        return view;
    }
}